executionPlan.queryExpressions:
define table daySpendLimitDataTable(consumerKey string, operatorId string,msisdn string, amount double) from ('datasource.name'='AXIATA_MIFE_DB', 'table.name'='day_spendlimitdata'); 

from SpendLimitData 
select consumerKey,operatorId, msisdn, chargeAmount, false as reset
insert into SpendLimitSummingStream;

from SpendLimitSummingStream#window.mife:cron('0 0 0 * * ?','msisdn')
select consumerKey,operatorId,msisdn, mife:optsum(chargeAmount,reset) as totalAmount, reset
group by consumerKey,msisdn
insert into totalStream;

from totalStream[reset==true]
delete daySpendLimitDataTable
on totalStream.msisdn == daySpendLimitDataTable.msisdn;

from totalStream[reset!=true ]
select consumerKey,operatorId,msisdn, totalAmount
insert into daySpendLimitDataTable;
