executionPlan.queryExpressions:
define table monthSpendLimitDataTable(consumerKey string, operatorId string,msisdn string, amount double) from ('datasource.name'='AXIATA_MIFE_DB', 'table.name'='month_spendlimitdata'); 

from SpendLimitData 
select consumerKey,operatorId, msisdn, chargeAmount, false as reset
insert into spendLimitSummingStream;

from spendLimitSummingStream#window.mife:cron('0 0 0 1 1/1 ? *','msisdn')
select consumerKey,operatorId,msisdn, mife:optsum(chargeAmount,reset) as totalAmount, reset
group by consumerKey, msisdn
insert into monthTotalStream;

from monthTotalStream[reset==true]
delete monthSpendLimitDataTable
on monthTotalStream.msisdn == monthSpendLimitDataTable.msisdn;

from monthTotalStream[reset!=true ]
select consumerKey,operatorId,msisdn, totalAmount
insert into monthSpendLimitDataTable;
